-- Create users_profiles table to store additional user data
create table users_profiles (
  id uuid primary key references auth.users(id) on delete cascade,
  name text not null,
  identification text not null,
  address text not null,
  kyc_status text default 'pending' check (kyc_status in ('pending', 'approved', 'rejected')),
  kyc_documents text[],
  referral_code text unique not null,
  referred_by text,
  created_at timestamp with time zone default now(),
  updated_at timestamp with time zone default now()
);

-- Enable RLS
alter table users_profiles enable row level security;

-- RLS Policies
create policy "Users can view their own profile"
  on users_profiles for select
  using (auth.uid() = id);

create policy "Users can update their own profile"
  on users_profiles for update
  using (auth.uid() = id);

create policy "Users can insert their own profile"
  on users_profiles for insert
  with check (auth.uid() = id);

-- Create function to generate unique referral code
create or replace function generate_referral_code()
returns text as $$
declare
  code text;
  exists boolean;
begin
  loop
    code := 'MXI' || upper(substring(md5(random()::text) from 1 for 6));
    select exists(select 1 from users_profiles where referral_code = code) into exists;
    exit when not exists;
  end loop;
  return code;
end;
$$ language plpgsql;

-- Create trigger to auto-generate referral code
create or replace function set_referral_code()
returns trigger as $$
begin
  if new.referral_code is null or new.referral_code = '' then
    new.referral_code := generate_referral_code();
  end if;
  return new;
end;
$$ language plpgsql;

create trigger before_insert_users_profiles
  before insert on users_profiles
  for each row
  execute function set_referral_code();

-- Create purchases table
create table purchases (
  id uuid primary key default gen_random_uuid(),
  user_id uuid references auth.users(id) on delete cascade not null,
  amount decimal not null,
  mxi_amount decimal not null,
  payment_method text not null check (payment_method in ('paypal', 'binance')),
  status text default 'pending' check (status in ('pending', 'completed', 'failed')),
  stage integer not null,
  created_at timestamp with time zone default now()
);

-- Enable RLS
alter table purchases enable row level security;

-- RLS Policies
create policy "Users can view their own purchases"
  on purchases for select
  using (auth.uid() = user_id);

create policy "Users can insert their own purchases"
  on purchases for insert
  with check (auth.uid() = user_id);

-- Create index for faster queries
create index purchases_user_id_idx on purchases(user_id);
create index purchases_created_at_idx on purchases(created_at desc);

-- Create vesting table
create table vesting (
  user_id uuid primary key references auth.users(id) on delete cascade,
  total_mxi decimal default 0,
  current_rewards decimal default 0,
  monthly_rate decimal default 0.03,
  last_update timestamp with time zone default now(),
  created_at timestamp with time zone default now()
);

-- Enable RLS
alter table vesting enable row level security;

-- RLS Policies
create policy "Users can view their own vesting"
  on vesting for select
  using (auth.uid() = user_id);

create policy "Users can insert their own vesting"
  on vesting for insert
  with check (auth.uid() = user_id);

create policy "Users can update their own vesting"
  on vesting for update
  using (auth.uid() = user_id);

-- Function to calculate vesting rewards
create or replace function calculate_vesting_rewards(p_user_id uuid)
returns decimal as $$
declare
  v_total_mxi decimal;
  v_current_rewards decimal;
  v_monthly_rate decimal;
  v_last_update timestamp with time zone;
  v_seconds_elapsed decimal;
  v_new_rewards decimal;
begin
  select total_mxi, current_rewards, monthly_rate, last_update
  into v_total_mxi, v_current_rewards, v_monthly_rate, v_last_update
  from vesting
  where user_id = p_user_id;

  if v_total_mxi is null then
    return 0;
  end if;

  v_seconds_elapsed := extract(epoch from (now() - v_last_update));
  v_new_rewards := (v_total_mxi * v_monthly_rate * v_seconds_elapsed) / (30 * 24 * 60 * 60);

  return v_current_rewards + v_new_rewards;
end;
$$ language plpgsql security definer;

-- Create referrals table
create table referrals (
  id uuid primary key default gen_random_uuid(),
  referrer_id uuid references auth.users(id) on delete cascade not null,
  referred_id uuid references auth.users(id) on delete cascade not null,
  level integer not null check (level in (1, 2, 3)),
  mxi_earned decimal default 0,
  created_at timestamp with time zone default now()
);

-- Enable RLS
alter table referrals enable row level security;

-- RLS Policies
create policy "Users can view their own referrals"
  on referrals for select
  using (auth.uid() = referrer_id);

create policy "System can insert referrals"
  on referrals for insert
  with check (true);

-- Create indexes
create index referrals_referrer_id_idx on referrals(referrer_id);
create index referrals_referred_id_idx on referrals(referred_id);

-- Function to process referral commissions
create or replace function process_referral_commission(
  p_user_id uuid,
  p_mxi_amount decimal
)
returns void as $$
declare
  v_referrer_id uuid;
  v_level integer := 1;
  v_commission_rate decimal;
  v_commission decimal;
begin
  -- Get the user's referrer
  select referred_by into v_referrer_id
  from users_profiles
  where id = p_user_id;

  -- Process up to 3 levels
  while v_referrer_id is not null and v_level <= 3 loop
    -- Calculate commission rate
    v_commission_rate := case v_level
      when 1 then 0.05
      when 2 then 0.02
      when 3 then 0.01
    end;

    v_commission := p_mxi_amount * v_commission_rate;

    -- Insert or update referral record
    insert into referrals (referrer_id, referred_id, level, mxi_earned)
    values (v_referrer_id, p_user_id, v_level, v_commission)
    on conflict (id) do nothing;

    -- Update referrer's vesting
    update vesting
    set total_mxi = total_mxi + v_commission
    where user_id = v_referrer_id;

    -- Get next level referrer
    select referred_by into v_referrer_id
    from users_profiles
    where id = v_referrer_id;

    v_level := v_level + 1;
  end loop;
end;
$$ language plpgsql security definer;

-- Create messages table
create table messages (
  id uuid primary key default gen_random_uuid(),
  user_id uuid references auth.users(id) on delete cascade not null,
  user_name text not null,
  message text not null,
  response text,
  status text default 'pending' check (status in ('pending', 'answered')),
  created_at timestamp with time zone default now(),
  updated_at timestamp with time zone default now()
);

-- Enable RLS
alter table messages enable row level security;

-- RLS Policies
create policy "Users can view their own messages"
  on messages for select
  using (auth.uid() = user_id);

create policy "Users can insert their own messages"
  on messages for insert
  with check (auth.uid() = user_id);

-- Create index
create index messages_user_id_idx on messages(user_id);
create index messages_status_idx on messages(status);

-- Create admin role check function
create or replace function is_admin()
returns boolean as $$
begin
  return exists (
    select 1 from auth.users
    where id = auth.uid()
    and email = 'admin@mxi.com'
  );
end;
$$ language plpgsql security definer;

-- Admin policies for users_profiles
create policy "Admins can view all profiles"
  on users_profiles for select
  using (is_admin());

create policy "Admins can update all profiles"
  on users_profiles for update
  using (is_admin());

-- Admin policies for purchases
create policy "Admins can view all purchases"
  on purchases for select
  using (is_admin());

create policy "Admins can update all purchases"
  on purchases for update
  using (is_admin());

-- Admin policies for vesting
create policy "Admins can view all vesting"
  on vesting for select
  using (is_admin());

create policy "Admins can update all vesting"
  on vesting for update
  using (is_admin());

-- Admin policies for messages
create policy "Admins can view all messages"
  on messages for select
  using (is_admin());

create policy "Admins can update all messages"
  on messages for update
  using (is_admin());

-- Admin policies for referrals
create policy "Admins can view all referrals"
  on referrals for select
  using (is_admin());

-- Admin policies for presale_stages
create policy "Admins can update presale stages"
  on presale_stages for update
  using (is_admin());

-- Create trigger to update vesting and process referrals on purchase completion
create or replace function on_purchase_completed()
returns trigger as $$
begin
  if new.status = 'completed' and old.status != 'completed' then
    -- Update or create vesting record
    insert into vesting (user_id, total_mxi, current_rewards, last_update)
    values (new.user_id, new.mxi_amount, 0, now())
    on conflict (user_id) do update
    set total_mxi = vesting.total_mxi + new.mxi_amount,
        last_update = now();

    -- Process referral commissions
    perform process_referral_commission(new.user_id, new.mxi_amount);

    -- Update stage sold amount
    update presale_stages
    set sold_mxi = sold_mxi + new.mxi_amount
    where stage = new.stage;
  end if;

  return new;
end;
$$ language plpgsql security definer;

create trigger after_purchase_update
  after update on purchases
  for each row
  execute function on_purchase_completed();

